bool Prime(int x)
{
	if (x <= 1) return false;
	if (x == 2) return true;
	if (x % 2 == 0) return false;
	for (int i = 3; i * i <= x; i += 2)
		if (x % i == 0) return false;
	return true;
}

int Get(int x)
{
	if (x == 0) return 0;
	if (Prime(x) && x != 2) return 1;
	if (x % 2 == 0 && Prime(x - 2)) return 4;
	if (x % 2 == 0) return 2;
	return 3;
}

int main()
{
	scanf("%d", &n);
	for (int i = 0; i < n; i++)
		scanf("%d", &x[i]);
	fill(dp, dp + n + 1, Inf); dp[0] = 0;
	for (int i = 0; i < n; i++) if (dp[i] < Inf) {
		int add = 0;
		for (int j = i; j < n; j++) {
			dp[j + 1] = min(dp[j + 1], dp[i] + add + Get(x[j] - x[i] + 1));
			if (j + 1 < n) add += Get(x[j + 1] - x[j] - 1);
		}
	}
	printf("%d\n", dp[n]);
	return 0;
}

