			for(i=first[u];~i;i=a[i].next){
				v=a[i].to;if(~dep[v]||!a[i].w) continue;
				dep[v]=dep[u]+1;q[tail++]=v;
			}
		}
		return ~dep[t];
	}
	int dfs(int u,int t,int lim){
		if(u==t||!lim) return lim;
		int i,v,f,flow=0;
