typedef Weight Flow;
typedef long long Cost;

const Cost CINF=1e+18;
const Flow FINF=1e+9;

//{{{ addFlowEdge(Graph,vector<vector<Cost> >, from,to,Flow,Cost)
void addFlowEdge(Graph &g,vector<vector<Cost> > &costv,int from,int to,Flow cap,Cost cost){
	g[from].push_back(Edge(from,to,cap,g[to].size()));
	g[to].push_back(Edge(to,from,0,g[from].size()-1));
	costv[from].push_back(cost);
	costv[to].push_back(-cost);
}
//}}}
//{{{ minCostFlow(Graph g,vector<vector<Cost> > cost,int s,int t,int f)
vector<vector<Flow> > flow;

Cost minCostFlow(const Graph &g,const vector<vector<Cost> > &cost,int s,int t,Flow f){
	const int n=g.size();
	flow.assign(n,vector<Flow>());
	REP(i,n)REP(j,g[i].size())flow[i].push_back(g[i][j].weight);
	vector<Cost> dist(n);
	vector<int> prevv(n),preve(n);
	Int res=0;
	while(f>0){
		fill(ALL(dist),CINF);
		dist[s]=0;
		bool update=true;
		while(update){
			update=false;
			for(int v=0;v<n;v++){
				if(dist[v]==CINF)continue;
				for(int i=0;i<g[v].size();i++){
					const Edge &e=g[v][i];
					if(flow[v][i]>0 && dist[e.dst]>dist[v]+cost[v][i]){
						dist[e.dst]=dist[v]+cost[v][i];
						prevv[e.dst]=v;preve[e.dst]=i;
						update=true;
					}
				}
			}
		}
		if(dist[t]==CINF){
			return -1;
		}
		Flow d=f;
		for(int v=t;v!=s;v=prevv[v]){
			d=min(d,flow[prevv[v]][preve[v]]);
		}
		f-=d;
		res+=d*dist[t];
		for(int v=t;v!=s;v=prevv[v]){
			const Edge &e=g[prevv[v]][preve[v]];
			flow[prevv[v]][preve[v]]-=d;
			flow[v][e.rev]+=d;
		}
	}
	return res;
}
