struct edge {int to, cap, rev;}; // rev Index
const int _V = 100 + 2; // _N + source + sink
vector<edge> G[_V];
bool used[_V];

void addEdge(int from, int to){
	G[from].push_back((edge) {to, 1, G[to].size()});
	G[to].push_back((edge) {from, 0, G[from].size() - 1}); // rev
}

int dfs(int v, int t){
	if(v==t)return 1;
	used[v] = true;
	REP(i, G[v].size()){
		edge &e = G[v][i];
		if(!used[e.to] && e.cap > 0){
			int d = dfs(e.to, t);
			e.cap -= d;
			G[e.to][e.rev].cap += d;
			return d;
		}
	}
	return 0;
}

int maxFlow(int s, int t){
	int flow=0;
	while(true){
		memset(used, 0, sizeof used);
		int p = dfs(s, t);
		if(!p)return flow;
		flow += p;
	}
}

