using namespace std;

const int MAXV = 10000100;

bool np[MAXV];

void precalc() {
   np[0] = np[1] = true;
   for (int i = 2; i < MAXV; ++i) {
      if (np[i]) continue;
      for (int j = i + i; j < MAXV; j += i) {
         np[j] = true;
      }
   }
}

int main() {
   ios_base::sync_with_stdio(false);
   precalc();
   int N;
   cin >> N;
   vector<int> X(N);
   for (int i = 0; i < N; ++i) {
      cin >> X[i];
   }
   {
      vector<int> newX;
      newX.emplace_back(X[0]);
      for (int i = 0; i + 1 < N; ++i) {
         if (X[i] + 1 != X[i + 1]) {
            newX.emplace_back(X[i] + 1);
            newX.emplace_back(X[i + 1]);
         }
      }
      newX.emplace_back(X.back() + 1);
      X = newX;
      N = X.size();
   }
   vector<int> odds;
   vector<int> evens;
   for (int i = 0; i < N; ++i) {
      if (X[i] & 1) {
         odds.emplace_back(X[i]);
      } else {
         evens.emplace_back(X[i]);
      }
   }
   int no = odds.size();
   int ne = evens.size();
   vector<vector<int>> adj(no);
   vector<int> mt(ne, -1);
   vector<bool> visited(no);
   for (int i = 0; i < no; ++i) {
      for (int j = 0; j < ne; ++j) {
         if (!np[abs(odds[i] - evens[j])]) {
            adj[i].emplace_back(j);
         }
      }
   }
   int tt = 0;
   function<bool(int)> dfs = [&](int v) {
      if (visited[v] == tt) return false;
      visited[v] = tt;
      for (int u : adj[v]) {
         if (mt[u] == -1) {
            mt[u] = v;
            return true;
         }
      }
      for (int u : adj[v]) {
         if (dfs(mt[u])) {
            mt[u] = v;
            return true;
         }
      }
      return false;
   };
   int ans = 0;
   for (int i = 0; i < no; ++i) {
      ++tt;
      ans += dfs(i);
   }
   no -= ans;
   ne -= ans;
   ans += 2 * (no / 2);
   ans += 2 * (ne / 2);
   if (no & 1) ans += 3;
   cout << ans << "\n";
   return 0;
}

