const int MaxN = 200;
const int MaxM = 10000000;
const int MaxNV = MaxN * 2;

int n;
bool f[MaxM + 2];

int nL, qL[MaxNV + 1];
int nR, qR[MaxNV + 1];

bool mat[MaxNV + 1][MaxNV + 1];

bool bookR[MaxNV + 1];
int mateR[MaxNV + 1];

int pri_n, pri[MaxM / 8];
bool book[MaxM + 2];

inline void init_sieve(int n = MaxM + 1)
{
	book[1] = true;
	for (int i = 2; i <= n; ++i)
	{
		if (!book[i])
			pri[pri_n++] = i;

		for (int j = 0; j < pri_n; ++j)
		{
			int p = pri[j], d = i * p;
			if (d > n)
				break;
			book[d] = true;

			if (i % p == 0)
				break;
		}
	}
}

bool match(const int &u)
{
	for (int v = 1; v <= nR; ++v)
		if (mat[u][v] && !bookR[v])
		{
			bookR[v] = true;
			if (!mateR[v] || match(mateR[v]))
			{
				mateR[v] = u;
				return true;
			}
		}
	return false;
}

int main()
{
	init_sieve();

	cin >> n;
	for (int i = 0, x; i < n; ++i)
	{
		cin >> x;
		f[x] = true;
	}

	nL = nR = 0;
	for (int i = 1; i <= MaxM + 1; ++i)
		if (f[i] != f[i - 1])
		{
			if (i & 1)
				qL[++nL] = i;
			else
				qR[++nR] = i;
		}

	for (int i = 1; i <= nL; ++i)
		for (int j = 1; j <= nR; ++j)
			if (!book[abs(qL[i] - qR[j])])
				mat[i][j] = true;

	int n_matches = 0;
	for (int u = 1; u <= nL; ++u)
	{
		for (int v = 1; v <= nR; ++v)
			bookR[v] = true;

		n_matches += match(u);
	}

	int res = n_matches;
	res += (nL - n_matches >> 1) << 1;
	res += (nR - n_matches >> 1) << 1;
	res += 3 * ((nL ^ n_matches) & 1);

	cout << res << endl;

	return 0;
}

