bitset<1<<24> did,pr;

int prime(int x) {
	if(x<=2) return 0;
	if(did[x]) return pr[x];
	did[x]=1;

	for(int a=2;a*a<=x;a++) {
		if(x%a==0) return pr[x]=0;;
	}
	return pr[x]=1;
}


int hoge(int L,int R) {
	if(R<=L) return 0;
	if(memo[L][R]>=0) return memo[L][R];
	if(L+1==R) return memo[L][R]=2;

	int ret=101010;
	for(int x=L+1;x<R;x++) ret=min(ret,hoge(L,x)+hoge(x,R));

	int ok=1,step=0;
	for(int i=L;i<R-1;i++) if(X[i+1]-X[i]!=1) {
		if(prime(X[i+1]-X[i]-1)) step++;
		else ok=0;
	}

	if(ok==1) {
		int len=X[R-1]-X[L]+1;
		if(len%2==0) ret=min(ret,2+step);
		else ret=min(ret,3+step);
	}


	return memo[L][R]=ret;


}

