	int x=0,f=1;char ch=getchar();
	for (;!isdigit(ch);ch=getchar()) if (ch=='-')  f=-f;
	for (;isdigit(ch);ch=getchar()) x=x*10+ch-'0';
	return x*f;
}

int main(){
	n=read();k=1;x.push_back(0);
	for (int i=1;i<=n;i++){
		int t=read();
		if (x.back()==t) x.pop_back();
		else x.push_back(t);
		x.push_back(t+1);
	}
	n=x.size()-1;
	for (int i=1;i<=n;i++)
		if (x[i]&1) add(n+1,i),cnt++;
		else {
			for (int j=1;j<=n;j++)
				if (check(x[i]+x[j])) add(j,i);
			add(i,n+2);
		}
	while (travel()) ans+=dinic(n+1,inf);
	printf("%d\n",n-ans+(cnt-ans)%2);
	return 0;
}

