#include<bits/stdc++.h>
using namespace std;
struct edge{
  long to,cap,rev;//cap, revv[to][rev]
  edge(){}
  edge(long t,long c,long r):to(t),cap(c),rev(r){}
};
class flow{
  long n;
  vector<vector<edge>>v;
  vector<bool>used;
  public:

  long dfs(long s,long t,long f){
    if(s==t)return f;
    used[s]=1;
    for(long i=0;i<v[s].size();i++){
      edge e=v[s][i];
      if(!used[e.to]&&e.cap>0){
        long r=dfs(e.to,t,min(f,e.cap));
        if(r>0){
          v[s][i].cap-=r;v[e.to][e.rev].cap+=r;
          return r;
        }
      }
    }
    return 0;
  }
  long solve(long s,long t){//
    long ans=0;
    while(1){
      used.clear();used=vector<bool>(n,0);
      long f=dfs(s,t,LONG_MAX);
      if(f==0)return ans;
      else ans+=f;
    }
  }
  flow(){}
  flow(const vector<vector<edge>>&v_)
    :n(v_.size()),v(vector<vector<edge>>(v_.size())){
      for(long i=0;i<n;i++)for(long j=0;j<v_[i].size();j++){
        edge e=v_[i][j];
        v[i].push_back(edge(e.to,e.cap,v[e.to].size()));//
        v[e.to].push_back(edge(i,0,v[i].size()-1));//
      }
    }
};

int main(){
  //
  long M=10000001;
  vector<bool>prime(M,1);prime[0]=0,prime[1]=0;
  for(long i=2;i<=M;i++){
    if(prime[i]){
      for(long j=2;i*j<M;j++)prime[i*j]=0;
    }
  }

  long N;cin>>N;
  vector<long>X(N);for(long i=0;i<N;i++)cin>>X[i];
  vector<long>O,E;
  for(long i=0;i<N;i++){
    if(i==0||X[i]-1!=X[i-1]){if(X[i]%2)O.push_back(X[i]);else E.push_back(X[i]);}
    if(i==N-1||X[i]+1!=X[i+1]){if((X[i]+1)%2)O.push_back(X[i]+1);else E.push_back(X[i]+1);}
  }

  vector<vector<edge>>A(2+O.size()+E.size());
  for(long i=0;i<O.size();i++)A[0].push_back(edge(1+i,LONG_MAX,0));
  for(long i=0;i<E.size();i++)A[1+O.size()+i].push_back(edge(1+O.size()+E.size(),LONG_MAX,0));
  for(long i=0;i<O.size();i++)for(long j=0;j<E.size();j++){
    long d=abs(O[i]-E[j]);
    if(prime[d])A[1+i].push_back(edge(1+O.size()+j,1,0));
  }
  flow f(A);long k=f.solve(0,1+O.size()+E.size());
  long ans=k+2*((O.size()-k)/2)+2*((E.size()-k)/2)+3*((O.size()-k)%2);
  cout<<ans;
}

