int my[MAXN];
bool visit[MAXN];

int dfs (int u)
{
	for (Int i = 0;i < G[u].size();++ i)
	{
		int v = G[u][i];
		if (visit[v]) continue;
		visit[v] = 1;
		if (my[v] == -1 || dfs (my[v]))
		{
			my[v] = u;
			return 1;
		}
	}
	return 0;
}

