bool isprime(int n) {
	if (n <= 2)return false;
	for (int i = 2; i*i <= n; i++) {
		if (n%i == 0)return false;
	}
	return true;
}
bool dfs(int v) {
	used[v] = true;
	if (v == S + 1) return true;
	for (int i = 0; i < S + 2; i++) {
		if (!used[i] && C[v][i] > 0) {
			if (dfs(i)) {
				C[v][i]--;
				C[i][v]++;
				return true;
			}
		}
	}
	return false;
}
