				C[a][b] = 1;
			}
		}
	}
	int ans = 0;
	while (dfs(S)) {
		memset(used, 0, sizeof(used));
		ans++;
	}
	if ((E - ans) % 2 == 0) {
		cout << ans + (E - ans + O - ans) << endl;
	}
	else {
		cout << ans + (E - ans - 1 + O - ans - 1) + 3 << endl;
	}
