void sieve() {
	memset(is_prime, 1, sizeof(is_prime));
	is_prime[0] = is_prime[1] = 0;
	for (int i = 2; i <= MAX_M; i++) {
		if (is_prime[i]) {
			primes.push_back(i);
			for (int j = i * 2; j <= MAX_M; j += i) is_prime[j] = 0;
		}
	}
}

bool dfs(int v) {
	used[v] = true;
	for (int i = 0; i < G[i].size(); i++) {
		int u = G[u][i], w = match[u];
		if (w < 0 || !used[u] && dfs(w)) {
			match[v] = u;
			match[u] = v;
			return true;
		}
	}
	return false;
}

void bipartite_matching() {
	memset(match, -1, sizeof(match));
	for (int v = 0; v < V; v++) {
		if (match[v] < 0) {
			memset(used, 0, sizeof(used));
			if (dfs(v)) {
				cnt++;
			}
		}
	}
}

