bool oddprime(int x) {
    x = abs(x);
    if (x <= 2) return 0;
    for (int i = 2; i*i <= x; ++i) if (x % i == 0) return 0;
    return 1;
}

int calc(int flow) {
    return flow+2*((y[0].size()-flow)/2+(y[1].size()-flow)/2)+3*((y[0].size()-flow)%2);    
}

void dfs(int x) {
    visit[x] = 1;
    for (int i: adj[x]) if (!visit[i]) {
        if (visit[201]) break;
        pre[i] = x;
        dfs(i);
    }
}

void backtrack(int x) {
    if (x == 0) return;
    int x1 = pre[x];
    adj[x1].erase(x);
    adj[x].insert(x1);
    backtrack(x1);
}

