class max_matching {
private:
	const int INF = 1e18;
	int match[N];
	int dis[N];
	vector<vector<int> > a;
	int n;
	bool bfs() {
		fill(dis, dis + N, INF);
		queue<int> q;
		for (int i = 1; i <= n; i++) {
			if (!match[i]) {
				dis[i] = 0;
				q.push(i);
			}
		}
		while (!q.empty()) {
			int u = q.front(); q.pop();
			if (u == 0) break;
			for (int i = 0; i < a[u].size(); i++) {
				int v = a[u][i]; v = match[v];
				if (dis[v] > dis[u] + 1) {
					dis[v] = dis[u] + 1;
					q.push(v);
				}
			}
		}
		return dis[0] != INF;
	}
	bool dfs(int u) {
		if (u == 0) return true;
		for (int i = 0; i < a[u].size(); i++) {
			int v = a[u][i];
			if (dis[match[v]] == dis[u] + 1 && dfs(match[v])) {
				match[u] = v;
				match[v] = u;
				return true;
			}
		}
		return false;
	}
public:
	int whmatch(int x) {
		return match[x];
	}
	max_matching(int _n) {
		n = _n;
		a.assign(n + 5, vector<int>());
		memset(match, 0, sizeof(match));
		memset(dis, 0, sizeof(dis));
	}
	void add_egde(int u, int v) {
		a[u].push_back(v); a[v].push_back(u);
	}
	int solve() {
		int ret = 0;
		while (bfs()) {
			for (int i = 1; i <= n; i++)
				if (match[i] == 0 && dfs(i)) ret++;
		}
		return ret;
	}
};

