using namespace std;

int N,M;
const int MAX_N = 111;
const int MAX_M = 444;

int a[MAX_N],b[MAX_N*2],sz;

struct edge{int u,cap,cost,rev;};
vector<edge> path[MAX_M];

void add_edge(int v,int u,int cap,int cost){
	path[v].push_back(edge{u,cap,cost,path[u].size()});
	path[u].push_back(edge{v,0,-cost,path[v].size()-1});
}

int get_cost(int x){
	if(x==1)return 1e9;
	if(x%2==0)return 2;
	for(int i=3;i*i<=x;i+=2){
		if(x%i==0)return 3;
	}
	return 1;
}

int p[MAX_M],pi[MAX_M],dist[MAX_M];

bool findPath(int S,int T){
	for(int i=0;i<=M;i++){
		dist[i]=1e9;
	}
	dist[S]=0;

	for(int t=0;t<=M;t++){
		for(int v=0;v<=M;v++){
			for(int i=0;i<path[v].size();i++){
				if(path[v][i].cap<=0)continue;
				int u=path[v][i].u;
				if(dist[u]>dist[v]+path[v][i].cost){
					dist[u]=dist[v]+path[v][i].cost;
					p[u]=v,pi[u]=i;
				}
			}
		}
	}

	return dist[T]<1e9;
}

int netFlow(int S,int T){
	int ret=0;
	while(findPath(S,T)){
		int f=1e9;
		for(int u=T,v;u!=S;u=v){
			v=p[u];
			f=min(f,path[v][pi[u]].cap);
		}
		for(int u=T,v;u!=S;u=v){
			v=p[u];
			path[v][pi[u]].cap-=f;
			path[u][path[v][pi[u]].rev].cap+=f;
		}

		ret+=f*dist[T];
	}
	return ret;
}

