	scanf("%d",&N);
	for(int i=1;i<=N;i++){
		scanf("%d",&a[i]);
	}
	a[0]=-1;
	a[N+1]=1e9;

	for(int i=1;i<=N+1;i++){
		if(a[i-1]+1<a[i]){
			if(i>1)b[sz++]=a[i-1]+1;
			if(i<=N)b[sz++]=a[i];
		}
	}

	M=sz*2+2;

	for(int i=0;i<sz;i++){
		for(int j=i+1;j<sz;j++){
			add_edge(i+2,j+2+sz,1,get_cost(b[j]-b[i]));
		}
	}
	for(int i=0;i<sz;i++){
		add_edge(1,i+2,1,0);
		add_edge(i+2+sz,sz*2+2,1,0);
	}
	add_edge(0,1,sz/2,0);

	printf("%d\n",netFlow(0,M));

	return 0;
}

