bool isprime(int n) {
	for (int i = 2; i*i < n; i++) {
		if (n%i == 0)return false;
	}
	return true;
}
bool dfs(int v) {
	used[v] = true;
	for (int i = 0; i < X.size() + 2; i++) {
		if (!used[i] && C[v][i] > 0) {
			if (dfs(i)) {
				C[v][i]--;
				C[i][v]++;
				return true;
			}
		}
	}
	return false;
}
int main() {
	cin >> n;
	vi evens;
	vi odds;
	for (int i = 0; i < n; i++) {
		int x;
		cin >> x;
		if (!X.empty() && X.back() == x) {
			X.pop_back();
			X.push_back(x + 1);
		}
		else {
			X.push_back(x);
			X.push_back(x + 1);
		}
	}
	int E = 0;
	int O = 0;
	for (int x : X) {
		if (x % 2 == 0) {
			E++;
		}
		else {
			O++;
		}
	}

	C.resize(X.size()+2, vi(X.size()+2, 0));
	for (int i = 0; i < X.size(); i++) {
		if (X[i] % 2 == 0) {
			C[n][i] = 1;
		}
		else {
			C[i][n + 1] = 1;
		}
	}
	for (int i = 0; i < X.size(); i++) {
		for (int j = i; j < n; j++) {
			if (isprime(abs(X[i] - X[j]))) {
				C[i][j] = 1;
			}
		}
	}
	int ans = 0;
	while (dfs(X.size())) {
		memset(used, false, sizeof(used));
		ans++;
	}
	if ((E - ans) % 2 == 0) {
		cout << ans + (E - ans + O - ans) << endl;
	}
	else {
		cout << ans + (E - ans - 1 + O - ans - 1) + 3 << endl;
	}
}

