	rep(i,1,mx+1) if (a[i]!=a[i-1]) {
		vec[i&1].push_back(i);
	}
	int ta=vec[0].size(),tb=vec[1].size();
	for (int i=0;i<ta;++i) {
		for (int j=0;j<tb;++j) {
			int d=abs(vec[0][i]-vec[1][j]);
			if (!np[d]&&(d&1)) {
				add_edge(i+1,j+1+ta);
			}
		}
	}
	int ans=0;
	rep(i,1,ta) ans+=find(i,i);
	ta-=ans,tb-=ans;
	ans+=(ta/2)*2+(tb/2)*2;
	if ((ta&1)||(tb&1)) ans+=3;
	printf("%d\n", ans);
	return 0;
}

