class PrimeFlip{
    int N = 1E7, n, m;
    unordered_set<int> cnt;
    vi odds,evens,mapping;
    vb prime,vist;
    vector<vb> E;
    void init(){
        int K;
        cin>>K;
        for(int i=0;i<K;++i){
            int x;
