        init();
        int cnt = 0;
        for(int i=0;i<n;++i) cnt += arange(i);
        int ans = cnt + ((n-cnt)/2 + (m-cnt)/2)*2 + ((m-cnt)%2)*3;
        cout<<ans<<endl;
    }
};



int main(){
    PrimeFlip().solve();
