	scanf("%d", &n);
	REP(i,1,n) { 
		int t;
		scanf("%d", &t);
		f[t] = 1;
	}
	vector<pii> A, B;
	int now = 0;
	REP(i,1,M-1) if (f[i]!=f[i-1]) {
		if (i&1) A.pb(pii(i,++now)),add(S,now,1);
		else B.pb(pii(i,++now)),add(now,T,1);
	}
	for (auto t:A) for (auto tt:B) { 
		if (chk(abs(t.x-tt.x))) {
			add(t.y,tt.y,1);
		}
	}
	int flow = dinic();
	int ans = flow, x = A.size()-flow, y = B.size()-flow;
	ans += x/2*2, x %= 2;
	ans += y/2*2, y %= 2;
	if (x) ans += 3;
	printf("%d\n", ans);
}

