template < int xn, int xe > struct flow
{
	int head[xn], next[xe], to[xe], fl[xe], N, E, cur[xn], dep[xn], q[xn + 1];
	inline void add(int u, int v, int f)
	{
		next[E] = head[u], to[E] = v, fl[E] = f, head[u] = E++;
		next[E] = head[v], to[E] = u, fl[E] = 0, head[v] = E++;
	}
	void clear(int n)
	{
		N = n, E = 0;
		for (int i = 0; i < N; i++)
			head[i] = -1;
	}
	bool BFS()
	{
		for (int i = 0; i < N; i++)
		{
			dep[i] = 0;
			cur[i] = head[i];
		}
		int H = 0, T = 1, u;
		dep[0] = 1;
		q[1] = 0;
		while (H < T)
			for (int e = head[u = q[++H]]; ~e; e = next[e])
				if (fl[e] && !dep[to[e]])
				{
					dep[q[++T] = to[e]] = dep[u] + 1;
					if (to[e] == N - 1)
						return true;
				}
		return false;
	}
	int DFS(int p, int f)
	{
		if (p == N - 1)
			return f;
		int u = 0, y;
		for (int &e = cur[p]; ~e; e = next[e])
			if (fl[e] && dep[to[e]] == dep[p] + 1 && (y = DFS(to[e], std::min(f, fl[e]))))
			{
				fl[e] -= y;
				fl[e ^ 1] += y;
				u += y;
				f -= y;
				if (!f)
					return u;
			}
		if (!u)
			dep[p] = 0;
		return u;
	}
	int operator () ()
	{
		int r = 0;
		while (BFS())
			r += DFS(0, 1000000000);
		return r;
	}
};
