using namespace std;
int n;
const int maxn = 1e7+10;//40M
const int S = 1e7+2;
int pri[maxn],cnt; bool mk[maxn];
int A[maxn];
void oula() {
	mk[1] = mk[0] = 1;
	for(int i=1;i<=S;i++) {
		if(!mk[i]) { pri[++cnt]=i; }
		for(int j=1;j<=cnt&&1ll*pri[j]*i<=S;j++) {
			int k = pri[j]*i;
			mk[k] = 1;
			if(i%pri[j]==0) break;
		}
	}
}
int la[1005],en[405*405],owo,nt[405*405];
void adg(int x,int y) {
	en[++owo]=y; nt[owo]=la[x]; la[x]=owo;
}
int cc[2];
int yo[2][1005];
int link[1005],vis[4005],vtm;
bool hungary(int x,int tm) {
	for(int it=la[x];it;it=nt[it]) {
		int y = en[it];
		if(vis[y]==tm) continue;
		vis[y] = tm;
		if((!link[y])||(hungary(link[y],tm))) {
			link[y] = x; return 1;
		}
	}
	return 0;
}
int main() {
	oula();
	scanf("%d",&n);
	for(int i=1;i<=n;i++) {
		int x; scanf("%d",&x); A[x] = 1;
	}
	for(int i=1;i<=S;i++) {
		int cf = A[i-1]^A[i];
		if(cf) {
			++cc[i&1];
			yo[i&1][cc[i&1]] = i;
		}
	}
	for(int i=1;i<=cc[0];i++) {
		for(int j=1;j<=cc[1];j++) {
			if(!mk[abs(yo[0][i]-yo[1][i])]) adg(i,j);
		}
	}
	int ans = 0;
	for(int i=1;i<=cc[0];i++) {
		ans += hungary(i,++vtm);
	}
	cc[0]-=ans; cc[1]-=ans;
	printf("%d",ans+cc[0]/2*2 + cc[1]/2*2 + (cc[0]&1)*3 );
}

#include<cstdio>
