int tot1=0,tot2=0;
bool find(int u)
{
	for(int i=1;i<=tot2;i++)
	{
		if(vis[i]==0&&e[u][i])
		{
			vis[i]=1;
			if(!match[i]||find(match[i]))
			{
				match[i]=u;
				return true;
			}
		}
	}
}
bool isprime(int x)
{
	if(x==1) return false;
	for(int i=2;i<=sqrt(x);i++)
		if(x%i==0)
			return false;
	return true;
}
int x[201];
int odd[201];
int even[201];
signed main()
{
	int n;
	scanf("%d",&n);
	for(int i=1;i<=n;i++)
		scanf("%d",&x[i]);
	for(int i=1;i<=n;i++)
	{
		if(i==1||x[i-1]!=x[i]-1)
		{
			if(x[i]%2==1) odd[++tot1]=x[i];
			else even[++tot2]=x[i];
		}
		if(i==n||x[i+1]!=x[i]+1)
		{
			if(x[i]%2==1) even[++tot2]=x[i]+1;
			else odd[++tot1]=x[i]+1;
		}
	}
	for(int i=1;i<=tot1;i++)
		for(int j=1;j<=tot2;j++)
			if(isprime(abs(odd[i]-even[j])))
				e[i][j]=1;
	int ans=0;
	for(int i=1;i<=tot1;i++)
	{
		memset(vis,0,sizeof(vis));
		if(find(i))
			ans++;
	}
	printf("%d",ans+(tot1-ans)/2*2+(tot2-ans)/2*2+(tot1-ans)%2*3);
	return 0;
}

