    }

    B.push_back(a_i_1 + 1);

    for(i=2;i<3163;i++){
        if(all_of(primes.begin(),primes.end(),[i](long long p) {return i % p != 0;})){
            primes.insert(i);
        }
    }

    for (i=0;i<B.size();i++){
        if (B[i] %2 == 0){
