int n, assigned[maxn], visited[maxn], t;
vector<int> a[maxn], even, odd;

int visit(int u) {
    if (visited[u]!=t) visited[u] = t;
    else return 0;
    REP(i, a[u].sz()) {
        int v = a[u][i];
        if (!assigned[v] || visit(assigned[v])) {
            assigned[v] = u;
            return 1;
        }
    }
    return 0;
}

int findMaximumMatching() {
    int cnt = 0;
    REP(i, even.sz()) {
        ++t;
        cnt += visit(i);
    }
    return cnt;
}

