class BipartiteMatching {
  int size;
  vector<vector<int>> g;
  vector<int> match;
  vector<bool> used;
  bool dfs(int v) {
    used[v] = true;
    for (int u: g[v]) {
      int w = match[u];
      if (w < 0 || (!used[w] && dfs(w))) {
        match[v] = u;
        match[u] = v;
        return true;
      }
    }
    return false;
  }
public:
  BipartiteMatching(int v) : size(v), g(v), match(v), used(v) {}
  void add_edge(int u, int v) {
    g[u].push_back(v);
    g[v].push_back(u);
  }
  int maximum_matching(void) {
    int res = 0;
    fill(begin(match), end(match), -1);
    for (int v = 0; v < size; ++v) {
      if (match[v] >= 0) continue;
      fill(begin(used), end(used), 0);
      if (dfs(v)) ++res;
    }
    return res;
  }
};

const int Prime_N = 1024000 * 10;

bitset<Prime_N> is_prime;

vector<int> primes() {
  is_prime.set();
  is_prime[0] = false;
  is_prime[1] = false;
  for (int i = 2; i * i < Prime_N; ++i) {
    if (is_prime[i]) {
      for (int j = i * i; j < Prime_N; j += i) {
        is_prime[j] = false;
      }
    }
  }
  vector<int> res;
  for (int i = 2; i < Prime_N; ++i) {
    if (is_prime[i]) res.push_back(i);
  }
  return res;
}

void add(vector<int> &v, int x) {
  auto it = find(ALL(v), x);
  if (it == end(v)) v.push_back(x);
  else v.erase(it);
}

void solve(ll N, vector<ll> x) {
  primes();
  vector<int> v;
  for (ll i: x) {
    add(v, i);
    add(v, i + 1);
  }
  sort(ALL(v));

  BipartiteMatching mat(N);
  REP(i,N) REP(j,N) {
    if (v[i] % 2 == 0 && v[j] % 2 == 1) {
      int d = abs(v[i] - v[j]);
      if (d >= 3 && is_prime[d]) mat.add_edge(i, j);
    }
  }
  int maxv = mat.maximum_matching();

  int even = 0, odd = 0;
  for (int i: v) {
    if (i % 2 == 0) ++even; else ++odd;
  }
  int res = 0;
  res += maxv;
  odd -= maxv; even -= maxv;

  res += even / 2 * 2;
  res += odd / 2 * 2;
  if (even % 2) res += 3;
  cout << res << endl;
}

