void build_prime () {
    for (int i=2; i<MAXC; i++) {
        if (!sieve[i]) {
            prime.emplace_back(i);
        }
        for (int j=0; j<SZ(prime)&&i*prime[j]<MAXC; j++) {
            sieve[i*prime[j]] = true;
            if (i % prime[j] == 0) {
