int pre[N],cur[N],hed[N],dep[N],gap[N],a[N],pri[M/10],val[E],nxt[E],to[E];
int query(int x){
	if(x==2||x==4)return 2;
	if(!vis[x])return 1;
	if(x&1)return 3;
	return 2;
}
void init(int n){
	vis[1]=1;
	for(int i=2;i<=n;i++){
		if(!vis[i]){pri[++cnt]=i;}
		for(int j=1;j<=cnt&&i*pri[j]<=n;j++){
			vis[i*pri[j]]=1;
			if(i%pri[j]==0)break;
		}
	}
}
void add_edge(int u,int v){
	nxt[tot]=hed[u],to[tot]=v,val[tot]=1,hed[u]=tot++;
	nxt[tot]=hed[v],to[tot]=u,val[tot]=0,hed[v]=tot++;
}
int isap(){
	int u=s,res=0;gap[0]=e;
	while(dep[s]<e){
		bool flag=1;
		for(int&i=cur[u];i;i=nxt[i]){
			if(val[i]&&dep[to[i]]+1==dep[u]){pre[to[i]]=i,u=to[i];flag=0;break;}
		}
		if(flag){
			if(!--gap[dep[u]])break;
			dep[u]=e;
			for(int i=hed[u];i;i=nxt[i]){
				if(val[i]&&dep[to[i]]+1<dep[u])dep[u]=dep[to[i]]+1,cur[u]=i;
			}
			++gap[dep[u]];
			if(u!=s)u=to[pre[u]^1];
		}
		if(u==e){
			for(int i=pre[e];i;i=pre[to[i^1]])val[i]--,val[i^1]++;
			u=s,res++;
		}
	}
	return res;
}
