void getPrime() {
    int n = 10000000;
    for (int i = 2; i <= n; ++i) {
        if (!notP[i])
            prime[tot++] = i;
        for (int j = 0; j < tot && i * prime[j] <= n; ++j) {
            notP[i * prime[j]] = true;
            if (i % prime[j] == 0)
                break;
        }
    }
