int mat[105], used[105], tag = 0;

int dfs(int u) {
    for (auto v : G[u]) {
        if (used[v] == tag)
            continue;
        used[v] = tag;
        if (mat[v] == -1 || dfs(mat[v])) {
            mat[v] = u;
            return 1;
        }
    }
    return 0;
}

