        int x;
        scanf("%d", &x);
        if (p != x - 1) {
            if (p != -1)
                A[(p + 1) & 1].push_back(p + 1);
            A[x & 1].push_back(x);
        }
        p = x;
    }
    A[(p + 1) & 1].push_back(p + 1);
    for (int i = 0; i < (int)A[0].size(); ++i) {
        for (int j = 0; j < (int)A[1].size(); ++j) {
            int d = abs(A[0][i] - A[1][j]);
            if (d >= 3 && !notP[d])
                G[i].push_back(j);
        }
    }
