bool isPrime(ll p) 
{
	for (ll i = 2; i <= sqrt(p); i++) if (p % i == 0) return false;
	return p > 1;
}

int main()
{
	int N;
	cin >> N;
	vector<int> x(N);
	map<int, int> y;
	REP(i, N) cin >> x[i];
	REP(i, N) {
		y[x[i]]++;
		y[x[i] + 1]++;
	}

	vector<int> odd;
	vector<int> even;
	for (auto it = y.begin(); it != y.end(); it++) {
		if (IsOdd(it->second)) {
			if (IsEven(it->first)) {
				even.emplace_back(it->first);
			} else {
				odd.emplace_back(it->first);
			}
		}
	}

	// 
	FordFulkerson res(4 * 1000);
	int start = 0;
	REP(i, even.size()) {
		res.AddEdge(start, 1000 + i, 1);
	}

	// 
	int end = 1;
	REP(i, odd.size()) {
		res.AddEdge(2000 + i, 1, 1);
	}

	// ()
	REP(i, even.size()) {
		REP(j, odd.size()) {
			if (isPrime(abs(even[i] - odd[j]))) {
				res.AddEdge(1000 + i, 2000 + j, 1);
			}
		}
	}

	// 
	int primeCnt = res.GetMaxFlow(start, end);

	// 
	int evenCnt = (even.size() - primeCnt) / 2 + (odd.size() - primeCnt) / 2;
	int oddCnt = (even.size() - primeCnt) % 2;

	cout << primeCnt + evenCnt * 2 + oddCnt * 3 << endl;
	return 0;
}

