const int maxN = 1003, maxE = 1000003, S = maxN - 2, T = maxN - 1, inf = 0x3f3f3f3f;
int cntNode;

int h[maxN], cntEdge = 1;
struct Edge {
    int tar, val, nxt;
    Edge() {}
    Edge(int tar, int val, int nxt) : tar(tar), val(val), nxt(nxt) {}
}ed[maxE];
inline void addEdge(int x, int y, int z) { ed[++cntEdge] = Edge(y, z, h[x]), h[x] = cntEdge, ed[++cntEdge] = Edge(x, 0, h[y]), h[y] = cntEdge; }

int cur[maxN], dep[maxN];
queue<int> que;
inline bool bfs() {
    int i, x, y;
    memset(dep + 1, 0, sizeof(int) * cntNode), memcpy(cur + 1, h + 1, sizeof(int) * cntNode), cur[S] = h[S], dep[S] = 1, dep[T] = 0, que.push(S);
    while (!que.empty()) {
        x = que.front(), que.pop();
        for (i = h[x]; i; i = ed[i].nxt)
            if (ed[i].val && !dep[y = ed[i].tar])
                dep[y] = dep[x] + 1, que.push(y);
    }
    return dep[T];
}
int dfs(int x, int flow) {
    if (x == T)
        return flow;
    int res = flow, k, i, y;
    for (i = cur[x]; i && res; i = ed[i].nxt) {
        cur[x] = i;
        if (ed[i].val && dep[y = ed[i].tar] == dep[x] + 1 && (k = dfs(y, min(res, ed[i].val))))
            res -= k, ed[i].val -= k, ed[i ^ 1].val += k;
    }
    return flow - res;
}

inline int Dinic() {
    int maxFlow = 0, k;
    while (bfs())
        while ((k = dfs(S, inf)))
            maxFlow += k;
    return maxFlow;
}

