        if (!notP[i])
            prime[++cntP] = i;
        for (j = 1; j <= lim && i * prime[j] <= lim; ++j) {
            notP[i * prime[j]] = 1;
            if (i % prime[j] == 0)
                break;
        }
    }

    for (i = 1; i <= n; ++i)
