inline int getint()
{
	int num=0,flag=1;char c;
	while((c=getchar())<'0'||c>'9')if(c=='-')flag=-1;
	while(c>='0'&&c<='9')num=num*10+c-48,c=getchar();
	return num*flag;
}

int n,N;
int a[maxn],w[maxn][maxn],link[maxn];
int pri[maxm],cnt;
bool np[maxm],col[maxm],vis[maxn];
int X[maxn],Y[maxn],ans;

inline void init()
{
	np[1]=1;
	for(int i=2;i<=N;i++)
	{
		if(!np[i])pri[++cnt]=i;
		for(int j=1;j<=cnt&&i*pri[j]<=N;j++)np[i*pri[j]]=1;
	}
}

inline bool match(int x)
{
	for(int i=1;i<=Y[0];i++)if(w[x][i]&&!vis[i])
	{
		if(vis[i]=1&&(!link[i]||match(link[i]))){link[i]=x;return 1;}
	}
	return 0;
}

int main()
{
	n=getint();
	for(int i=1;i<=n;i++)N=max(N,a[i]=getint()),col[a[i]]=1;
	N++;init();
	for(int i=1;i<=N;i++)if(col[i]^col[i-1])i&1?X[++X[0]]=i:Y[++Y[0]]=i;
	for(int i=1;i<=X[0];i++)for(int j=1;j<=Y[0];j++)w[i][j]=!np[abs(X[i]-Y[j])];
	for(int i=1;i<=X[0];i++)
	{
		memset(vis,0,sizeof vis);
		ans+=match(i);
	}
	ans+=2*((X[0]-ans)/2)+2*((Y[0]-ans)/2)+3*((X[0]-ans)&1);
	printf("%d\n",ans);
}

