void getPrime() {
    pchk = vector<int>(10000010, 0);
    for(int i = 2; i < 10000010; i++) {
        if(pchk[i]) continue;
        int mul = 2;
        while(i * mul < 10000010) {
            pchk[i * mul] = 1;
            mul++;
        }
    }
}

int N;
vector<int> X;

int main() {
    getPrime();

    scanf("%d", &N);
    X.resize(N);
    for(int i = 0; i < N; i++) {
        scanf("%d", &X[i]);
    }
    int ans = 3*N;

    int pre = 1;
    int tmp = 0;
    for(int i = 0; i < N; i++) {
        tmp += (X[i] - pre) % 2 == 0? (X[i] - pre == 0? 0 : 2) : (pchk[ X[i] - pre ]? 3 : 1);
        pre = X[i] + 1;
        //cout<<tmp<<endl;
    }
    if(pchk[ X[N - 1] ]) tmp += (X[N - 1] % 2 == 0? 2 : 3);
    else tmp++;
    ans = min(ans, tmp);
    cout<<ans;
}

