int read(){
	int x=0,f=1;char ch=getchar();
	while(ch<'0'||ch>'9'){if(ch=='-') f=-1;ch=getchar();}
	while(ch>='0'&&ch<='9'){x=x*10+ch-'0';ch=getchar();}
	return f*x;
}
void add_edge(int from,int to){
	edge[++num_edge].nxt=head[from];
	edge[num_edge].to=to;
	head[from]=num_edge;
}
bool find(int x){
	for(int i=head[x];i;i=edge[i].nxt){
		if(vis[edge[i].to]!=tim){
			vis[edge[i].to]=tim;
			if(!lk[edge[i].to]||find(edge[i].to)){
				lk[edge[i].to]=x;
				return 1;
			}
		}
	}
	return 0;
}
bool check(int x){
	if(x==1) return 0;
	for(int i=2;i*i<=x;++i){
		if(x%i==0) return 0;
	}
	return 1;
}
int main(){
//	freopen("ex.in","r",stdin);
	n=read();
	for(int i=1;i<=n;++i) b[i]=read();b[0]=-INF;b[n+1]=INF;
	for(int i=1;i<=n;++i){
		if(b[i]!=b[i-1]+1){
			if(i%2) odd[++odd_num]=b[i];
			else even[++even_num]=b[i];
		}
		if(b[i]+1!=b[i+1]){
			if((i+1)%2) odd[++odd_num]=b[i]+1;
			else even[++even_num]=b[i]+1;
		}
	}
	for(int i=1;i<=odd_num;++i){
		for(int j=1;j<=even_num;++j){
			if(check(abs(even[j]-odd[i]))) add_edge(even[j],odd[i]);
		}
	}
	for(int i=1;i<=odd_num;++i){
		tim++;
		if(find(odd[i])) k++;
		else break;
	}
	ans=k+((even_num-k)/2+(odd_num-k)/2)*2+((even_num-k)%2)*3;
	printf("%d",ans);
	return 0;
}

