#define Rep(i, n) for (int i = 1; i <= n; i ++)

using namespace std;

const int N = 210, N0 = 1e7 + 10;

int d0[N], d[N], cd;

bool isnotprime[N0];
int prime[N0], cnt;
void get_prime()
{
	isnotprime[1] = true;
	for (int i = 2; i <= 1e7; i ++){
		if (!isnotprime[i]) prime[++ cnt] = i;
		for (int j = 1; j <= cnt && i * prime[j] <= 1e7; j ++) {
			isnotprime[i * prime[j]] = true;
			if (!(i % prime[j])) break;
		}
	}
}

bool g[N][N], vis[N];
int lnk[N];

bool dfs(int x)
{
	vis[x] = true;
	Rep(i, cd) if (g[x][i] && !vis[i]){
		if (lnk[i] == -1 || dfs(lnk[i])) {
			lnk[i] = x;
			return true;
		}
	}
	return false;
}

int main()
{
	int n;
	scanf("%d", &n);
	Rep(i, n) scanf("%d", &d0[i]);
	d0[0] = -1;
	Rep(i, n) {
		if (d0[i - 1] != d0[i] - 1) d[++ cd] = d0[i];
		if (d0[i + 1] != d0[i] + 1) d[++ cd] = d0[i] + 1;
	}

	get_prime();
	Rep(i, cd) if (d[i] & 1)
		Rep(j, cd) if (!(d[j] & 1))
			if (!isnotprime[(int)abs(d[j] - d[i])]) g[i][j] = true;

	int c0 = 0, c1 = 0, cc = 0;
	Rep(i, cd) lnk[i] = -1;
	Rep(i, cd) {
		//printf("%d\n", d[i]);
		if (d[i] & 1) {
			Rep(j, cd) vis[j] = false;
			if (dfs(i)) cc ++;
			c1 ++;
		}
		else c0 ++;
	}
	//printf("%d %d %d\n", cc, c0, c1);
	int ans = cc;
	c0 -= cc, c1 -= cc;
	ans += (c0 / 2) * 2, c0 %= 2;
	ans += (c1 / 2) * 2, c1 %= 2;
	if (c0 && c1) ans += 3;

	printf("%d\n", ans);

	return 0;
}

