	for (int i = 1; i <= n; ++ i)
	{
		F[i] = INF;
		for (int j = 0; j < i; ++ j)
		{
			F[i] = min(F[i], F[j]+calc(j+1, i));
		}
	}
	cout << F[n] << endl;

    return 0;
}

