bool bo[10001000],vis[210];
int st[210],mp[210][210],part[210],top,js0,js1;
bool isprime(int p)
{
	if ((p<=2)||(!(p&1))) return false;
	for (int i=2;i*i<=p;i++) if (p%i==0) return false;
	return true;
}
bool search(int s)
{
	vis[s]=true;
	for (int i=1;i<=top;i++) if (mp[s][i])
	{
		if (!part[i]) {part[i]=s;return true;}
		else if ((!vis[i])&&(search(part[i]))) {part[i]=s;return true;}
	}
	return false;
}
int main()
{
	int n;scanf("%d",&n);
	for (int i=1;i<=n;i++) {int w;scanf("%d",&w);bo[w]=true;}
	for (int i=1;i<=10000001;i++) if (bo[i]^bo[i-1]) {st[++top]=i;if (i&1) js1++;else js0++;}
	for (int i=1;i<=top;i++) if (st[i]&1)
	for (int j=1;j<=top;j++) if (isprime(abs(st[i]-st[j]))) mp[i][j]=1;
	int sum=0;
	for (int i=1;i<=top;i++) if (st[i]&1)
	{
		memset(vis,0,sizeof(vis));
		sum+=search(i);
	}
	js0-=sum;js1-=sum;
	cout<<sum+js0+js1+(js0&js1&1)<<endl;
}

