  cout<<fixed<<setprecision(0);
	int n;
	cin>>n;
	vector<int> xs(n);
	rep(i,n) cin>>xs[i];

	auto Cost=[&](int d){
		if(!d) return 0;
		if(d==4) return 2;
		if(d==1) return 3;
		if(d%2==0) return 2;
		if(d>2 && prime[d]==0) return 1;
		return 3;
	};

	reps(d,1,n+1)rep(i,n+1-d){
		for(int l=i,r=i+d-1; l<=r; ++l, --r){
			if(r-l==xs[r]-xs[l]){
				dp[i][i+d-1]=Cost(xs[r]-xs[l]+1);
				break;
			}
			if(l==r) dp[i][i+d-1]+=Cost(1);
			else dp[i][i+d-1]+=Cost(xs[r]-xs[l]+1)+Cost(xs[r]-xs[l]-1);
		}
		reps(c,i,i+d-1) MN(dp[i][i+d-1], dp[i][c]+dp[c+1][i+d-1]);
	}
	//rep(i,n){rep(j,n)cout<<dp[i][j]<<",";cout<<endl;}
	cout<<dp[0][n-1]<<endl;
