inline int GetLen1(int uu, int vv)
{
	return len1[uu][vv];
}

inline int GetLen2(int uu, int vv)
{
	return len2[uu][vv];
}

inline int GetTms(int xx)
{
	if (xx == 1)
	{
		return 3;
	}
	if (!(xx & 1))
	{
		return 2;
	}
	else if (!chk[xx])
	{
		return 1;
	}
	else
	{
		return 3;
	}
}

int main()
{
