		e[++ecnt]=(edge){fl,t,head[f]};head[f]=ecnt;
		e[++ecnt]=(edge){0,f,head[t]};head[t]=ecnt;
	}
	//----------------
	int S,T;
	int dis[N],cur[N];
	bool BFS(){
		memset(dis,0x3f,sizeof(dis));
		dis[S]=0;queue<int> que;que.push(S);
		while(!que.empty()){
			int u=que.front();que.pop();
			for(int i=head[u];i;i=e[i].next){
				if(e[i].flow!=0&&dis[e[i].t]==0x3f3f3f3f){
					dis[e[i].t]=dis[u]+1,que.push(e[i].t);
				}
			}
		}
		return dis[T]!=INF;
	}

	int DFS(int u,int a){
		if(u==T||a==0) return a;
		int ret=0;
		for(int &i=cur[u];i;i=e[i].next){
			if(e[i].flow==0||dis[e[i].t]!=dis[u]+1) continue;
			int f=DFS(e[i].t,a<e[i].flow? a:e[i].flow);
			if(f){
				e[i].flow-=f;e[i^1].flow+=f;
				ret+=f;a-=f;
				if(a==0) return ret;
			}
		}
		return ret;
	}
	int work(int S1,int T1){
		S=S1,T=T1;
		int ret=0;
		while(BFS()){
			memcpy(cur,head,sizeof(cur));
			ret+=DFS(S1,INF);
		}
		return ret;
	}
	void initialize(){
		ecnt=1;memset(head,0,sizeof(head));
	}
	#undef N
	#undef M
	#undef INF
}

