    for(i=2;i*i<=a;i++)if(a%i==0)return false;
    return true;
}
int MaxFlow(int a){
    if(a==sink)return 1;
    v[a] = cur;
    int i;
    for(i=0;i<=sink;i++){
        if(F[a][i] && v[i]!=cur){
            if(MaxFlow(i)){
                F[a][i]--;
                F[i][a]++;
                return 1;
            }
        }
    }
    return 0;
}
