	}
	std::vector<int> b;
	int f[2] = {0, 0};
	for(int i = 1; i < ms; i++) {
		if(a[i]) {
			b.push_back(i);
			f[i%2]++;
		}
	}
	n = (int) b.size();
	Dinic<int> graph(n+2);
	int src = n, sink = n+1;
