/* [size, maxlen, v[0], v[1], ...] */
int *vector_init(){
  int *v;
  int n;
  n = 256;
  v = malloc(4 * (2+n));
  *(v+0) = 0; /* v[0] := size */
  *(v+1) = 4; /* v[1] := maxlen */
  return v;
}
int *vector_push_back(int *vector, int value){
  int size;
  int maxlen;
  size = *(vector+0);
  maxlen = *(vector+1);
  if(size == maxlen){
    maxlen = maxlen * 2;
    vector = realloc(vector, 4 * (2+maxlen));
    *(vector+1) = maxlen;
  }
  *(vector+2+size) = value;
  size = size + 1;
  *(vector+0) = size;
  return vector;
}
int *vector_pop_back(int *vector){
  int size;
  size = *(vector+0);
  if(size == 0){
    return vector;
  }else{
    *(vector+0) = size - 1;
    return vector;
  }
}
int *vector_back(int *vector){
  int size;
  size = *(vector+0);
  if(size==0)return vector;
  return vector+1+size;
}

int calc_max_flow(int n, int **cap, int s, int t){
  int flow;
  int i;
  flow = 0;
  while(1){
    int cur;
    int *bef;
    int *visited;
    int *stack;
    bef = malloc(4 * n);
    visited = malloc(4 * n);
    i = 0;
    while(i < n){
      *(bef+i) = -1;
      *(visited+i) = 0;
      i = i + 1;
    }
    stack = vector_init();
    stack = vector_push_back(stack, s);
    *(visited+s) = 1;
    while(!(*(stack+0) == 0)){
      int p;
      p = *vector_back(stack);
      stack = vector_pop_back(stack);
      if(p==t)goto goal;
      i = 0;
      while(i < n){
        if(*(*(cap+p)+i)==1 && !*(visited+i)){
          *(visited+i) = 1;
          *(bef+i) = p;
          stack = vector_push_back(stack, i);
        }
        i = i + 1;
      }
    }
goal:
    free(stack);
    free(visited);
    if(*(bef+t)==-1){
      free(bef);
      return flow;
    }
    flow = flow + 1;
    /* reverse */
    cur = t;
    while(1){
      int nxt;
      nxt = *(bef+cur);
      if(nxt == -1)goto reverse_end;
      *(*(cap+nxt)+cur) = 0;
      *(*(cap+cur)+nxt) = 1;
      cur = nxt;
    }
reverse_end:
    free(bef);
  }
  return flow;
}

