            return res;
        }

        private boolean dfs(int v, boolean[][] g, boolean[] was, int[] pair) {
            if (was[v]) {
                return false;
            }
            was[v] = true;
            for (int u = 0; u < g[v].length; u++) {
                if (!g[v][u]) {
                    continue;
