/**
 * Built using CHelper plug-in Actual solution is at the top
 * 
 * @author daltao
 */
public class Main {
    public static void main(String[] args) throws Exception {
        Thread thread = new Thread(null, new TaskAdapter(), "daltao", 1 << 27);
        thread.start();
        thread.join();
    }

    static class TaskAdapter implements Runnable {
        @Override
        public void run() {
            InputStream inputStream = System.in;
            OutputStream outputStream = System.out;
            FastInput in = new FastInput(inputStream);
            FastOutput out = new FastOutput(outputStream);
            TaskD solver = new TaskD();
            solver.solve(1, in, out);
            out.close();
        }
    }
    static class TaskD {
        NumberTheory.EulerSieve es = new NumberTheory.EulerSieve(10000000);
        int[][][] dp;
        int[] xs;
        int n;

        public void solve(int testNumber, FastInput in, FastOutput out) {
            n = in.readInt();
            xs = new int[n];
            for (int i = 0; i < n; i++) {
                xs[i] = in.readInt();
            }

            dp = new int[2][n][n];
            SequenceUtils.deepFill(dp, -1);
            int ans = dp(0, n - 1);
            out.println(ans);
        }

        public int feeOnFlip(int len) {
            if (len == 0) {
                return 0;
            }
            if (len == 2) {
                return 2;
            }
            if (es.isPrime(len)) {
                return 1;
            }
            return len % 2 + 2;
        }

        public int dp(int l, int r) {
            if (dp[0][l][r] == -1) {
                if (l == r) {
                    return dp[0][l][r] = feeOnFlip(1);
                }
                dp[0][l][r] = feeOnFlip(xs[r] - xs[l] + 1) + dpFlipped(l, r);
                for (int i = l; i < r; i++) {
                    dp[0][l][r] = Math.min(dp[0][l][r], dp(l, i) + dp(i + 1, r));
                }
            }
            return dp[0][l][r];
        }

        public int dpFlipped(int l, int r) {
            if (dp[1][l][r] == -1) {
                if (l == r) {
                    return dp[1][l][r] = 0;
                }
                if (l + 1 == r) {
                    return dp[1][l][r] = feeOnFlip(xs[r] - xs[l] - 1);
                }
                // flip or sep
                dp[1][l][r] = feeOnFlip(xs[r] - xs[l] - 1) + dp(l + 1, r - 1);
                for (int i = l + 1; i < r; i++) {
                    dp[1][l][r] = Math.min(dp[1][l][r], dpFlipped(l, i) + dpFlipped(i, r));
                }
            }
            return dp[1][l][r];
        }

    }
    static class NumberTheory {
        public static class EulerSieve {
            private int[] primes;
            private boolean[] isComp;
            private int primeLength;

            public boolean isPrime(int x) {
                if (x == 1) {
                    return false;
                }
                return !isComp[x];
            }

            public EulerSieve(int limit) {
                isComp = new boolean[limit + 1];
                primes = new int[limit + 1];
                primeLength = 0;
                for (int i = 2; i <= limit; i++) {
                    if (!isComp[i]) {
                        primes[primeLength++] = i;
                    }
                    for (int j = 0, until = limit / i; j < primeLength && primes[j] <= until; j++) {
                        int pi = primes[j] * i;
                        isComp[pi] = true;
                        if (i % primes[j] == 0) {
                            break;
                        }
                    }
                }
            }

        }

    }
    static class SequenceUtils {
        public static void deepFill(Object array, int val) {
            if (!array.getClass().isArray()) {
                throw new IllegalArgumentException();
            }
            if (array instanceof int[]) {
                int[] intArray = (int[]) array;
                Arrays.fill(intArray, val);
            } else {
                Object[] objArray = (Object[]) array;
                for (Object obj : objArray) {
                    deepFill(obj, val);
                }
            }
        }

    }
    static class FastInput {
        private final InputStream is;
        private byte[] buf = new byte[1 << 13];
        private int bufLen;
        private int bufOffset;
        private int next;

        public FastInput(InputStream is) {
            this.is = is;
        }

        private int read() {
            while (bufLen == bufOffset) {
                bufOffset = 0;
                try {
                    bufLen = is.read(buf);
                } catch (IOException e) {
                    bufLen = -1;
                }
                if (bufLen == -1) {
                    return -1;
                }
            }
            return buf[bufOffset++];
        }

        public void skipBlank() {
            while (next >= 0 && next <= 32) {
                next = read();
            }
        }

        public int readInt() {
            int sign = 1;

            skipBlank();
            if (next == '+' || next == '-') {
                sign = next == '+' ? 1 : -1;
                next = read();
            }

            int val = 0;
            if (sign == 1) {
                while (next >= '0' && next <= '9') {
                    val = val * 10 + next - '0';
                    next = read();
                }
            } else {
                while (next >= '0' && next <= '9') {
                    val = val * 10 - next + '0';
                    next = read();
                }
            }

            return val;
        }

    }
    static class FastOutput implements AutoCloseable, Closeable {
        private StringBuilder cache = new StringBuilder(10 << 20);
        private final Writer os;

        public FastOutput(Writer os) {
            this.os = os;
        }

        public FastOutput(OutputStream os) {
            this(new OutputStreamWriter(os));
        }

        public FastOutput println(int c) {
            cache.append(c).append('\n');
            return this;
        }

        public FastOutput flush() {
            try {
                os.append(cache);
                os.flush();
                cache.setLength(0);
            } catch (IOException e) {
                throw new UncheckedIOException(e);
            }
            return this;
        }

        public void close() {
            flush();
            try {
                os.close();
            } catch (IOException e) {
                throw new UncheckedIOException(e);
            }
        }

    }
}

import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.*;

public class Main {
    static long __startTime = System.currentTimeMillis();

    public static void main(String[] args) {
        InputReader in = new InputReader(System.in);
        PrintWriter out = new PrintWriter(System.out);

        int n = in.nextInt();

        int[] x = new int[n];
        for (int i = 0; i < n ; i++) {
            x[i] = in.nextInt();
        }

        Set<Integer> set = new TreeSet<>();
        for (int i = 0; i < n ; i++) {
            for (int a = x[i] ; a <= x[i]+1; a++) {
                if (set.contains(a)) {
                    set.remove(a);
                } else {
                    set.add(a);
                }
            }
        }

        List<Integer> r = new ArrayList<>(set);
        int nodes = r.size();
        MaxFlowDinic maxFlow = new MaxFlowDinic();
        maxFlow.init(nodes+2);

        int src = nodes;
        int dst = nodes+1;

        int odd = 0;
        int even = 0;
        for (int i = 0; i < nodes ; i++) {
            if (r.get(i) % 2 == 0) {
                even++;
                maxFlow.edge(src, i, 1);
            } else {
                odd++;
                maxFlow.edge(i, dst, 1);
            }
        }
        for (int i = 0; i < nodes ; i++) {
            for (int j = 0 ; j < nodes ; j++) {
                if (r.get(i) % 2 == 0 && r.get(j) % 2 == 1) {
                    int d = Math.abs(r.get(i) - r.get(j));
                    if (isPrime(d)) {
                        maxFlow.edge(i, j, 1);
                    }
                }
            }
        }

        int F = maxFlow.max_flow(src, dst);
        odd -= F;
        even -= F;

        int total = F + (odd / 2) * 2 + (even / 2) * 2;
        if (odd % 2 == 1 && even % 2 == 1) {
            total += 3;
        }
        out.println(total);
        out.flush();
    }

    static boolean isPrime(int x) {
        if (x == 1) {
            return false;
        }
        for (int i = 2 ; i * i <= x ; i++) {
            if (x % i == 0) {
                return false;
            }
        }
        return true;
    }

    public static class MaxFlowDinic {
        public List<int[]>[] graph;
        public int[] deg;

        public int[] level;
        public int[] itr;

        public int[] que;

        @SuppressWarnings("unchecked")
        public void init(int size) {
            graph = new List[size];
            for (int i = 0; i < size ; i++) {
                graph[i] = new ArrayList<int[]>();
            }
            deg = new int[size];
            level = new int[size];
            itr = new int[size];
            que = new int[size+10];
        }
        public void edge(int from, int to, int cap) {
            int fdeg = deg[from];
            int tdeg = deg[to];
            graph[from].add(new int[]{to, cap, tdeg});
            graph[to].add(new int[]{from, 0, fdeg});
            deg[from]++;
            deg[to]++;
        }

        public int dfs(int v, int t, int f) {
            if (v == t) return f;
            for (int i = itr[v] ; i < graph[v].size() ; i++) {
                itr[v] = i;
                int[] e = graph[v].get(i);
                if (e[1] > 0 && level[v] < level[e[0]]) {
                    int d = dfs(e[0], t, Math.min(f, e[1]));
                    if (d > 0) {
                        e[1] -= d;
                        graph[e[0]].get(e[2])[1] += d;
                        return d;
                    }
                }
            }
            return 0;
        }

        public void bfs(int s) {
            Arrays.fill(level, -1);
            int qh = 0;
            int qt = 0;
            level[s] = 0;
            que[qh++] = s;
            while (qt < qh) {
                int v = que[qt++];
                for (int i = 0; i < graph[v].size() ; i++) {
                    int[] e = graph[v].get(i);
                    if (e[1] > 0 && level[e[0]] < 0) {
                        level[e[0]] = level[v] + 1;
                        que[qh++] = e[0];
                    }
                }
            }
        }

        public int max_flow(int s, int t) {
            int flow = 0;
            while (true) {
                bfs(s);
                if (level[t] < 0) {
                    return flow;
                }
                Arrays.fill(itr, 0);
                while (true) {
                    int f = dfs(s, t, Integer.MAX_VALUE);
                    if (f <= 0) {
                        break;
                    }
                    flow += f;
                }
            }
        }
    }

